mapper.select:
select
id, load_rct_id, accountability_type, remark, accountability_state, create_by, create_time, server_ip,
update_by, update_time, del_flag
from loan_accountability

    del_flag = 0

        and id = #{id}


        and load_rct_id = #{loadRctId}


        and accountability_type = #{accountabilityType}


        and remark = #{remark}


        and accountability_state = #{accountabilityState}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and server_ip = #{serverIp}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}
