mapper.select:
select
id, dept_id,dept_name, parent_id, user_id, user_name, reward_amount, loan_type, create_by, create_time, server_ip,
update_by, update_time, del_flag
from ruoyi.loan_reward_config

    del_flag = 0

        and dept_name like concat ('%',#{deptName},'%')


        and id = #{id}


        and dept_id = #{deptId}


        and parent_id = #{parentId}


        and user_id = #{userId}


        and user_name = #{userName}


        and reward_amount = #{rewardAmount}


        and loan_type = #{loanType}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and server_ip = #{serverIp}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}
